mapper.select:
select * 
from `order`

	
		and order_id=#{order_id}
	
	
		and user_id=#{user_id}
	
	
		and product_id=#{product_id}
	
	
		and product_price >= #{orderPriceLowerBound}
	
	
		and product_price <= #{orderPriceUpperBound}
	
	
		and order_date >= #{oderDateLowerBound}
	
	
		and order_date <= #{oderDateUpperBound}
